      icon: "🧩",
      component: PuzzleGame,
    },
    {
      id: "dino-game",
      title: "Dino Run",
      description: "Jump over obstacles in this endless runner game!",
      icon: "🦕",
      component: DinoGame,
    },
    {
      id: "flappy-bird",
