import { useState, useEffect, useCallback, useRef } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { RotateCcw, Trophy, Shuffle, Check } from "lucide-react";

interface PuzzlePiece {
  id: number;
  x: number;
  y: number;
  width: number;
  height: number;
  correctX: number;
  correctY: number;
  path: string;
  color: string;
  isPlaced: boolean;
}

interface PuzzleGameProps {
  onBack: () => void;
}

const COLORS = [
  '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
  '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43',
  '#10AC84', '#EE5A24', '#0984E3', '#6C5CE7', '#A29BFE'
];

const LANDSCAPE_SCENES = [
  {
    name: "Forest Walk",
    description: "People walking through a green forest path",
    generateScene: (width: number, height: number) => {
      const elements = [];

      // Background sky (light blue)
      elements.push({
        type: 'rect',
        props: { x: 0, y: 0, width, height: height * 0.4, fill: '#87CEEB' }
      });

      // Background hills (light green)
      elements.push({
        type: 'path',
        props: { 
          d: `M 0 ${height * 0.35} Q ${width * 0.3} ${height * 0.25} ${width * 0.6} ${height * 0.35} Q ${width * 0.8} ${height * 0.4} ${width} ${height * 0.3} L ${width} ${height * 0.4} L 0 ${height * 0.4} Z`,
          fill: '#90EE90'
        }
      });

      // Ground (darker green)
      elements.push({
        type: 'rect',
        props: { x: 0, y: height * 0.4, width, height: height * 0.6, fill: '#228B22' }
      });

      // Trees (various sizes and positions)
      const treePositions = [
        { x: width * 0.1, scale: 0.8 },
        { x: width * 0.25, scale: 1.2 },
        { x: width * 0.4, scale: 0.9 },
        { x: width * 0.6, scale: 1.1 },
        { x: width * 0.75, scale: 0.7 },
        { x: width * 0.9, scale: 1.0 }
      ];

      treePositions.forEach((pos, i) => {
        const treeHeight = 80 * pos.scale;
        const treeWidth = 40 * pos.scale;
        const treeY = height * 0.4 + (height * 0.3);

        // Tree trunk
        elements.push({
          type: 'rect',
          props: { 
            x: pos.x - 4 * pos.scale, 
            y: treeY, 
            width: 8 * pos.scale, 
            height: 30 * pos.scale, 
            fill: '#8B4513' 
          }
        });

        // Tree crown (circle)
        elements.push({
          type: 'circle',
          props: { 
            cx: pos.x, 
            cy: treeY - 15 * pos.scale, 
            r: treeWidth / 2, 
            fill: i % 2 === 0 ? '#006400' : '#228B22' 
          }
        });
      });

      // Walking path
      elements.push({
        type: 'ellipse',
        props: { 
          cx: width * 0.5, 
          cy: height * 0.8, 
          rx: width * 0.3, 
          ry: 20, 
          fill: '#DEB887' 
        }
      });

      // People walking (simple figures)
      const people = [
        { x: width * 0.45, y: height * 0.75, color: '#FF6B6B' },
        { x: width * 0.55, y: height * 0.75, color: '#4ECDC4' }
      ];

      people.forEach(person => {
        // Body
        elements.push({
          type: 'rect',
          props: { x: person.x - 3, y: person.y - 15, width: 6, height: 15, fill: person.color }
        });
        // Head
        elements.push({
          type: 'circle',
          props: { cx: person.x, cy: person.y - 20, r: 4, fill: '#FDBCB4' }
        });
        // Arms
        elements.push({
          type: 'line',
          props: { x1: person.x - 3, y1: person.y - 10, x2: person.x - 8, y2: person.y - 5, stroke: person.color, strokeWidth: 2 }
        });
        elements.push({
          type: 'line',
          props: { x1: person.x + 3, y1: person.y - 10, x2: person.x + 8, y2: person.y - 5, stroke: person.color, strokeWidth: 2 }
        });
        // Legs
        elements.push({
          type: 'line',
          props: { x1: person.x - 1, y1: person.y, x2: person.x - 4, y2: person.y + 8, stroke: '#4169E1', strokeWidth: 2 }
        });
        elements.push({
          type: 'line',
          props: { x1: person.x + 1, y1: person.y, x2: person.x + 4, y2: person.y + 8, stroke: '#4169E1', strokeWidth: 2 }
        });
      });

      // Clouds
      const clouds = [
        { x: width * 0.2, y: height * 0.1 },
        { x: width * 0.7, y: height * 0.15 }
      ];

      clouds.forEach(cloud => {
        elements.push({
          type: 'circle',
          props: { cx: cloud.x, cy: cloud.y, r: 15, fill: 'white', opacity: 0.8 }
        });
        elements.push({
          type: 'circle',
          props: { cx: cloud.x + 12, cy: cloud.y, r: 12, fill: 'white', opacity: 0.8 }
        });
        elements.push({
          type: 'circle',
          props: { cx: cloud.x + 6, cy: cloud.y - 8, r: 10, fill: 'white', opacity: 0.8 }
        });
      });

      return elements;
    }
  }
];

export default function PuzzleGame({ onBack }: PuzzleGameProps) {
  const [pieces, setPieces] = useState<PuzzlePiece[]>([]);
  const [draggedPiece, setDraggedPiece] = useState<PuzzlePiece | null>(null);
  const [score, setScore] = useState<number>(0);
  const [placedCount, setPlacedCount] = useState<number>(0);
  const [totalPieces, setTotalPieces] = useState<number>(0);
  const [isCompleted, setIsCompleted] = useState<boolean>(false);
  const [currentPattern, setCurrentPattern] = useState<string>("");
  const [showPreview, setShowPreview] = useState<boolean>(true);
  const [countdown, setCountdown] = useState<number>(5);

  const puzzleAreaRef = useRef<HTMLDivElement>(null);
  const [puzzleSize, setPuzzleSize] = useState({ width: 300, height: 300 });

  // Generate random puzzle
  const generatePuzzle = useCallback(() => {
    const scene = LANDSCAPE_SCENES[0]; // Use forest scene
    const pieceSize = 60;
    const rows = 5;
    const cols = 4;
    const totalPcs = rows * cols;

    setCurrentPattern(scene.name);
    setTotalPieces(totalPcs);
    setPlacedCount(0);
    setScore(0);
    setIsCompleted(false);
    setShowPreview(true);
    setCountdown(5);

    const newPieces: PuzzlePiece[] = [];

    // Generate the landscape scene
    const sceneWidth = cols * pieceSize;
    const sceneHeight = rows * pieceSize;
    const sceneElements = scene.generateScene(sceneWidth, sceneHeight);

    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        const id = row * cols + col;
        const correctX = col * pieceSize;
        const correctY = row * pieceSize;

        // Create a rectangular path for each piece
        const path = `M 2 2 L ${pieceSize - 2} 2 L ${pieceSize - 2} ${pieceSize - 2} L 2 ${pieceSize - 2} Z`;

        // Use a color from the scene or default
        const color = COLORS[id % COLORS.length];

        // Random starting position in pieces area
        const startX = Math.random() * 200;
        const startY = Math.random() * 100;

        newPieces.push({
          id,
          x: startX,
          y: startY,
          width: pieceSize,
          height: pieceSize,
          correctX,
          correctY,
          path,
          color,
          isPlaced: false,
        });
      }
    }

    setPieces(newPieces);
    setPuzzleSize({ width: cols * pieceSize, height: rows * pieceSize });

    // Store scene elements for rendering
    (window as any).currentSceneElements = sceneElements;

    // Start countdown
    const countdownInterval = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          setShowPreview(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  }, []);

  // Check if piece is in correct position
  const isInCorrectPosition = useCallback((piece: PuzzlePiece, x: number, y: number) => {
    const tolerance = 30; // Increased tolerance for easier mobile play
    return (
      Math.abs(x - piece.correctX) < tolerance &&
      Math.abs(y - piece.correctY) < tolerance
    );
  }, []);

  // Handle piece placement
  const handlePiecePlacement = useCallback((piece: PuzzlePiece, x: number, y: number) => {
    if (isInCorrectPosition(piece, x, y)) {
      // Snap to correct position - SUCCESS
      setPieces(prev => prev.map(p => 
        p.id === piece.id 
          ? { ...p, x: p.correctX, y: p.correctY, isPlaced: true }
          : p
      ));

      setScore(prev => prev + 10);
      setPlacedCount(prev => {
        const newCount = prev + 1;
        if (newCount === totalPieces) {
          setIsCompleted(true);
        }
        return newCount;
      });
    } else {
      // WRONG POSITION - Return to pieces area with visual feedback
      setPieces(prev => prev.map(p => 
        p.id === piece.id 
          ? { 
              ...p, 
              x: Math.random() * 150 + 25, // Random position in pieces area
              y: Math.random() * 80 + 20,   // Spread pieces better
            }
          : p
      ));

      // Visual feedback for wrong placement (could add shake animation later)
      console.log("Wrong position! Piece returned to collection area.");
    }
  }, [isInCorrectPosition, totalPieces]);

  // Mouse/Touch handlers for drag and drop
  const handleMouseDown = useCallback((piece: PuzzlePiece, e: React.MouseEvent) => {
    if (piece.isPlaced) return;
    e.preventDefault();
    setDraggedPiece(piece);
  }, []);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (!draggedPiece) return;

    const rect = puzzleAreaRef.current?.getBoundingClientRect();
    if (!rect) return;

    const x = e.clientX - rect.left - draggedPiece.width / 2;
    const y = e.clientY - rect.top - draggedPiece.height / 2;

    setPieces(prev => prev.map(p => 
      p.id === draggedPiece.id ? { ...p, x, y } : p
    ));
  }, [draggedPiece]);

  const handleMouseUp = useCallback(() => {
    if (!draggedPiece) return;

    const piece = pieces.find(p => p.id === draggedPiece.id);
    if (piece) {
      // Only allow placement in puzzle target area (top area)
      const isInPuzzleArea = piece.y >= 0 && piece.y <= puzzleSize.height + 50;

      if (isInPuzzleArea) {
        handlePiecePlacement(piece, piece.x, piece.y);
      } else {
        // If dropped outside puzzle area, return to pieces area
        setPieces(prev => prev.map(p => 
          p.id === piece.id 
            ? { 
                ...p, 
                x: Math.random() * 150 + 25,
                y: Math.random() * 80 + 20,
              }
            : p
        ));
      }
    }

    setDraggedPiece(null);
  }, [draggedPiece, pieces, handlePiecePlacement, puzzleSize]);

  // Touch handlers
  const handleTouchStart = useCallback((piece: PuzzlePiece, e: React.TouchEvent) => {
    if (piece.isPlaced) return;
    e.preventDefault();
    setDraggedPiece(piece);
  }, []);

  const handleTouchMove = useCallback((e: React.TouchEvent) => {
    if (!draggedPiece) return;
    e.preventDefault(); // Prevent scrolling during drag

    const rect = puzzleAreaRef.current?.getBoundingClientRect();
    if (!rect) return;

    const touch = e.touches[0];
    const x = touch.clientX - rect.left - draggedPiece.width / 2;
    const y = touch.clientY - rect.top - draggedPiece.height / 2;

    setPieces(prev => prev.map(p => 
      p.id === draggedPiece.id ? { ...p, x, y } : p
    ));
  }, [draggedPiece]);

  const handleTouchEnd = useCallback(() => {
    if (!draggedPiece) return;

    const piece = pieces.find(p => p.id === draggedPiece.id);
    if (piece) {
      // Only allow placement in puzzle target area (top area)  
      const isInPuzzleArea = piece.y >= 0 && piece.y <= puzzleSize.height + 50;

      if (isInPuzzleArea) {
        handlePiecePlacement(piece, piece.x, piece.y);
      } else {
        // If dropped outside puzzle area, return to pieces area
        setPieces(prev => prev.map(p => 
          p.id === piece.id 
            ? { 
                ...p, 
                x: Math.random() * 150 + 25,
                y: Math.random() * 80 + 20,
              }
            : p
        ));
      }
    }

    setDraggedPiece(null);
  }, [draggedPiece, pieces, handlePiecePlacement, puzzleSize]);

  // Initialize with first puzzle
  useEffect(() => {
    generatePuzzle();
  }, [generatePuzzle]);

  return (
    <div className="fixed inset-0 bg-background flex flex-col overflow-hidden">
      {/* Mobile Header */}
      <div className="flex items-center justify-between p-3 bg-muted/20 border-b shrink-0">
        <Button variant="outline" size="sm" onClick={onBack}>
          ← Geri
        </Button>

        {/* Game Stats */}
        <div className="flex items-center gap-2 text-xs">
          <div className="flex items-center gap-1">
            <span className="font-bold">{score}</span>
            <span className="text-muted-foreground">score</span>
          </div>
          <div className="w-px h-3 bg-muted"></div>
          <div className="flex items-center gap-1">
            <span className="font-bold">{placedCount}</span>
            <span className="text-muted-foreground">/ {totalPieces}</span>
          </div>
        </div>

        {/* Control Button */}
        <Button onClick={generatePuzzle} size="sm" variant="outline">
          <Shuffle className="h-3 w-3" />
        </Button>
      </div>

      {/* Preview Modal */}
      {showPreview && (
        <div className="absolute inset-0 bg-background/95 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <Card className="border-blue-500 max-w-md w-full">
            <CardContent className="p-6 text-center">
              <h2 className="text-xl font-bold mb-4">Preview - {currentPattern}</h2>
              <div className="mb-4 flex justify-center">
                <div 
                  className="border-2 border-blue-500 rounded-lg bg-white overflow-hidden"
                  style={{
                    width: '240px',
                    height: '300px',
                  }}
                >
                  <svg width="240" height="300" viewBox="0 0 240 300">
                    {/* Sky */}
                    <rect x="0" y="0" width="240" height="120" fill="#87CEEB" />

                    {/* Hills */}
                    <path d="M 0 105 Q 72 75 144 105 Q 192 120 240 90 L 240 120 L 0 120 Z" fill="#90EE90" />

                    {/* Ground */}
                    <rect x="0" y="120" width="240" height="180" fill="#228B22" />

                    {/* Trees */}
                    <g>
                      {/* Tree 1 */}
                      <rect x="22" y="200" width="6" height="24" fill="#8B4513" />
                      <circle cx="25" cy="188" r="16" fill="#006400" />

                      {/* Tree 2 */}
                      <rect x="58" y="190" width="8" height="30" fill="#8B4513" />
                      <circle cx="62" cy="175" r="20" fill="#228B22" />

                      {/* Tree 3 */}
                      <rect x="92" y="195" width="7" height="27" fill="#8B4513" />
                      <circle cx="95.5" cy="182" r="18" fill="#006400" />

                      {/* Tree 4 */}
                      <rect x="140" y="185" width="8" height="32" fill="#8B4513" />
                      <circle cx="144" cy="170" r="22" fill="#228B22" />

                      {/* Tree 5 */}
                      <rect x="176" y="200" width="6" height="22" fill="#8B4513" />
                      <circle cx="179" cy="189" r="14" fill="#006400" />

                      {/* Tree 6 */}
                      <rect x="212" y="195" width="7" height="25" fill="#8B4513" />
                      <circle cx="215.5" cy="182" r="16" fill="#228B22" />
                    </g>

                    {/* Walking path */}
                    <ellipse cx="120" cy="240" rx="72" ry="12" fill="#DEB887" />

                    {/* People */}
                    <g>
                      {/* Person 1 */}
                      <rect x="105" y="210" width="6" height="15" fill="#FF6B6B" />
                      <circle cx="108" cy="205" r="4" fill="#FDBCB4" />
                      <line x1="105" y1="215" x2="100" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                      <line x1="111" y1="215" x2="116" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                      <line x1="107" y1="225" x2="104" y2="233" stroke="#4169E1" strokeWidth="2" />
                      <line x1="109" y1="225" x2="112" y2="233" stroke="#4169E1" strokeWidth="2" />

                      {/* Person 2 */}
                      <rect x="129" y="210" width="6" height="15" fill="#4ECDC4" />
                      <circle cx="132" cy="205" r="4" fill="#FDBCB4" />
                      <line x1="129" y1="215" x2="124" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                      <line x1="135" y1="215" x2="140" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                      <line x1="131" y1="225" x2="128" y2="233" stroke="#4169E1" strokeWidth="2" />
                      <line x1="133" y1="225" x2="136" y2="233" stroke="#4169E1" strokeWidth="2" />
                    </g>

                    {/* Clouds */}
                    <g opacity="0.8">
                      <circle cx="48" cy="30" r="15" fill="white" />
                      <circle cx="60" cy="30" r="12" fill="white" />
                      <circle cx="54" cy="22" r="10" fill="white" />

                      <circle cx="168" cy="45" r="15" fill="white" />
                      <circle cx="180" cy="45" r="12" fill="white" />
                      <circle cx="174" cy="37" r="10" fill="white" />
                    </g>
                  </svg>
                </div>
              </div>
              <div className="text-2xl font-bold text-blue-500 mb-2">{countdown}</div>
              <p className="text-sm text-muted-foreground">seconds remaining</p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Completion Modal */}
      {isCompleted && (
        <div className="absolute inset-0 bg-background/90 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <Card className="border-green-500 max-w-sm w-full">
            <CardContent className="p-4 text-center">
              <Trophy className="h-8 w-8 mx-auto mb-3 text-green-500" />
              <h2 className="text-lg font-bold mb-2">Congratulations!</h2>
              <div className="text-sm mb-4 space-y-1">
                <p>Puzzle completed!</p>
                <p>Pattern: {currentPattern}</p>
                <p>Final Score: {score}</p>
              </div>
              <Button onClick={generatePuzzle} size="sm" className="w-full">
                New Puzzle
              </Button>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Game Area */}
      <div className="flex-1 p-2 overflow-hidden">
        <div 
          ref={puzzleAreaRef}
          className="relative w-full h-full"
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          {/* Puzzle Target Area */}
          <div className="absolute top-4 left-1/2 transform -translate-x-1/2">
            <div 
              className="relative border-2 border-dashed border-muted-foreground/30 rounded-lg bg-muted/10"
              style={{
                width: puzzleSize.width,
                height: puzzleSize.height,
              }}
            >
              {/* Guide grid */}
              <div className="absolute inset-0 opacity-20">
                {pieces.map((piece) => (
                  <div
                    key={`guide-${piece.id}`}
                    className="absolute border border-muted-foreground/20 rounded-sm"
                    style={{
                      left: piece.correctX,
                      top: piece.correctY,
                      width: piece.width,
                      height: piece.height,
                    }}
                  />
                ))}
              </div>

              {/* Placed pieces - showing actual landscape sections */}
              {pieces.filter(piece => piece.isPlaced).map((piece) => {
                const row = Math.floor(piece.id / 4);
                const col = piece.id % 4;
                const viewBoxX = col * 60;
                const viewBoxY = row * 60;

                return (
                  <div
                    key={`placed-${piece.id}`}
                    className="absolute cursor-default overflow-hidden rounded border border-white/50"
                    style={{
                      left: piece.x,
                      top: piece.y,
                      width: piece.width,
                      height: piece.height,
                    }}
                  >
                    <svg width={piece.width} height={piece.height} viewBox={`${viewBoxX} ${viewBoxY} 60 60`}>
                      {/* Sky */}
                      <rect x="0" y="0" width="240" height="120" fill="#87CEEB" />

                      {/* Hills */}
                      <path d="M 0 105 Q 72 75 144 105 Q 192 120 240 90 L 240 120 L 0 120 Z" fill="#90EE90" />

                      {/* Ground */}
                      <rect x="0" y="120" width="240" height="180" fill="#228B22" />

                      {/* Trees */}
                      <rect x="22" y="200" width="6" height="24" fill="#8B4513" />
                      <circle cx="25" cy="188" r="16" fill="#006400" />
                      <rect x="58" y="190" width="8" height="30" fill="#8B4513" />
                      <circle cx="62" cy="175" r="20" fill="#228B22" />
                      <rect x="92" y="195" width="7" height="27" fill="#8B4513" />
                      <circle cx="95.5" cy="182" r="18" fill="#006400" />
                      <rect x="140" y="185" width="8" height="32" fill="#8B4513" />
                      <circle cx="144" cy="170" r="22" fill="#228B22" />
                      <rect x="176" y="200" width="6" height="22" fill="#8B4513" />
                      <circle cx="179" cy="189" r="14" fill="#006400" />
                      <rect x="212" y="195" width="7" height="25" fill="#8B4513" />
                      <circle cx="215.5" cy="182" r="16" fill="#228B22" />

                      {/* Walking path */}
                      <ellipse cx="120" cy="240" rx="72" ry="12" fill="#DEB887" />

                      {/* People */}
                      <rect x="105" y="210" width="6" height="15" fill="#FF6B6B" />
                      <circle cx="108" cy="205" r="4" fill="#FDBCB4" />
                      <line x1="105" y1="215" x2="100" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                      <line x1="111" y1="215" x2="116" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                      <line x1="107" y1="225" x2="104" y2="233" stroke="#4169E1" strokeWidth="2" />
                      <line x1="109" y1="225" x2="112" y2="233" stroke="#4169E1" strokeWidth="2" />

                      <rect x="129" y="210" width="6" height="15" fill="#4ECDC4" />
                      <circle cx="132" cy="205" r="4" fill="#FDBCB4" />
                      <line x1="129" y1="215" x2="124" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                      <line x1="135" y1="215" x2="140" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                      <line x1="131" y1="225" x2="128" y2="233" stroke="#4169E1" strokeWidth="2" />
                      <line x1="133" y1="225" x2="136" y2="233" stroke="#4169E1" strokeWidth="2" />

                      {/* Clouds */}
                      <g opacity="0.8">
                        <circle cx="48" cy="30" r="15" fill="white" />
                        <circle cx="60" cy="30" r="12" fill="white" />
                        <circle cx="54" cy="22" r="10" fill="white" />
                        <circle cx="168" cy="45" r="15" fill="white" />
                        <circle cx="180" cy="45" r="12" fill="white" />
                        <circle cx="174" cy="37" r="10" fill="white" />
                      </g>
                    </svg>
                    <Check className="absolute top-1 right-1 h-3 w-3 text-green-500 bg-white rounded-full p-0.5" />
                  </div>
                );
              })}
            </div>
          </div>

          {/* Moveable pieces - showing landscape sections to drag */}
          {pieces.filter(piece => !piece.isPlaced).map((piece) => {
            const row = Math.floor(piece.id / 4);
            const col = piece.id % 4;
            const viewBoxX = col * 60;
            const viewBoxY = row * 60;

            return (
              <div
                key={`piece-${piece.id}`}
                className="absolute cursor-move hover:scale-105 transition-transform overflow-hidden rounded border-2 border-white shadow-lg"
                style={{
                  left: piece.x,
                  top: piece.y + puzzleSize.height + 80,
                  width: piece.width,
                  height: piece.height,
                  zIndex: draggedPiece?.id === piece.id ? 1000 : 1,
                }}
                onMouseDown={(e) => handleMouseDown(piece, e)}
                onTouchStart={(e) => handleTouchStart(piece, e)}
              >
                <svg width={piece.width} height={piece.height} viewBox={`${viewBoxX} ${viewBoxY} 60 60`}>
                  {/* Sky */}
                  <rect x="0" y="0" width="240" height="120" fill="#87CEEB" />

                  {/* Hills */}
                  <path d="M 0 105 Q 72 75 144 105 Q 192 120 240 90 L 240 120 L 0 120 Z" fill="#90EE90" />

                  {/* Ground */}
                  <rect x="0" y="120" width="240" height="180" fill="#228B22" />

                  {/* Trees */}
                  <rect x="22" y="200" width="6" height="24" fill="#8B4513" />
                  <circle cx="25" cy="188" r="16" fill="#006400" />
                  <rect x="58" y="190" width="8" height="30" fill="#8B4513" />
                  <circle cx="62" cy="175" r="20" fill="#228B22" />
                  <rect x="92" y="195" width="7" height="27" fill="#8B4513" />
                  <circle cx="95.5" cy="182" r="18" fill="#006400" />
                  <rect x="140" y="185" width="8" height="32" fill="#8B4513" />
                  <circle cx="144" cy="170" r="22" fill="#228B22" />
                  <rect x="176" y="200" width="6" height="22" fill="#8B4513" />
                  <circle cx="179" cy="189" r="14" fill="#006400" />
                  <rect x="212" y="195" width="7" height="25" fill="#8B4513" />
                  <circle cx="215.5" cy="182" r="16" fill="#228B22" />

                  {/* Walking path */}
                  <ellipse cx="120" cy="240" rx="72" ry="12" fill="#DEB887" />

                  {/* People */}
                  <rect x="105" y="210" width="6" height="15" fill="#FF6B6B" />
                  <circle cx="108" cy="205" r="4" fill="#FDBCB4" />
                  <line x1="105" y1="215" x2="100" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                  <line x1="111" y1="215" x2="116" y2="220" stroke="#FF6B6B" strokeWidth="2" />
                  <line x1="107" y1="225" x2="104" y2="233" stroke="#4169E1" strokeWidth="2" />
                  <line x1="109" y1="225" x2="112" y2="233" stroke="#4169E1" strokeWidth="2" />

                  <rect x="129" y="210" width="6" height="15" fill="#4ECDC4" />
                  <circle cx="132" cy="205" r="4" fill="#FDBCB4" />
                  <line x1="129" y1="215" x2="124" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                  <line x1="135" y1="215" x2="140" y2="220" stroke="#4ECDC4" strokeWidth="2" />
                  <line x1="131" y1="225" x2="128" y2="233" stroke="#4169E1" strokeWidth="2" />
                  <line x1="133" y1="225" x2="136" y2="233" stroke="#4169E1" strokeWidth="2" />

                  {/* Clouds */}
                  <g opacity="0.8">
                    <circle cx="48" cy="30" r="15" fill="white" />
                    <circle cx="60" cy="30" r="12" fill="white" />
                    <circle cx="54" cy="22" r="10" fill="white" />
                    <circle cx="168" cy="45" r="15" fill="white" />
                    <circle cx="180" cy="45" r="12" fill="white" />
                    <circle cx="174" cy="37" r="10" fill="white" />
                  </g>
                </svg>
              </div>
            );
          })}

          {/* Instructions */}
          {!showPreview && (
            <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-center">
              <div className="bg-background/80 backdrop-blur-sm rounded-lg p-3 max-w-xs">
                <p className="text-xs text-muted-foreground mb-1">
                  Pattern: <span className="font-semibold text-foreground">{currentPattern}</span>
                </p>
                <p className="text-xs text-muted-foreground mb-1">
                  Drag forest pieces to rebuild the landscape
                </p>
                <p className="text-xs text-red-600 font-semibold">
                  ⚠️ Must place pieces exactly in correct position or they return!
                </p>
                <div className="mt-3 p-2 bg-yellow-50 dark:bg-yellow-900/20 rounded border-l-2 border-yellow-400">
                  <p className="text-xs text-yellow-800 dark:text-yellow-200 font-medium">
                    🔧 Bəzi düzəlişlər gedir - Some improvements are in progress
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
